import org.koin.core.module.dsl.viewModelOf
import org.koin.dsl.module
import uniffi.heidi_credentials_rust.SignatureCreator
import uniffi.heidi_crypto_rust.SoftwareKeyPair
import uniffi.heidi_crypto_rust.base64UrlDecode
import uniffi.heidi_crypto_rust.parseEncodedJwtPayload
import uniffi.heidi_dcql_rust.DcqlQuery
import uniffi.heidi_util_rust.Value
import kotlin.String
import kotlin.uuid.ExperimentalUuidApi
import kotlin.uuid.Uuid

@OptIn(ExperimentalUuidApi::class)
class ProximityViewModel : ViewModel(), KoinComponent {

	companion object {
		val koinModule = module {
			viewModelOf(::ProximityViewModel)
		}
	}

	private lateinit var wallet: ProximityWallet

	private val proximityStateMutable = MutableStateFlow<ProximityWalletState>(ProximityWalletState.Initial)
	val proximityState = proximityStateMutable.asStateFlow()

	override fun onCleared() {
		super.onCleared()
		wallet.disconnect()
	}

	fun startEngagement(qrCodeData: String) {
		viewModelScope.launch {
//			val qrCodeUri = Uri.parse(qrCodeData)
//			val verifierName = qrCodeUri.getQueryParameter("name")!!
//			val publicKey = qrCodeUri.getQueryParameter("key")
//			val serviceUuid = Uuid.parse(qrCodeUri.getQueryParameter("uuid")!!)

			wallet = ProximityWallet.createReverse(ProximityProtocol.MDL, viewModelScope, qrCodeData)
			wallet.startEngagement("Blub")

			startCollectingWalletState()
		}
	}

	/**
	 * Start a engagement via QR-Code for a ISO mdl flow
	 */
	fun startEngagementMdl() {
		viewModelScope.launch {
			val serviceUuid = Uuid.random()
			val peripheralUuid = Uuid.random()
			// The wallet chooses to use one of the two (or both modes).
			wallet = ProximityWallet.create(ProximityProtocol.MDL, viewModelScope, serviceUuid, peripheralUuid)
			wallet.startEngagement("")
			startCollectingWalletState()
		}
	}

	val ISSUER_DID = "did:<...>"
	val ISSUER_PRIVATE_KEY = "secret scalar of the p256 curve b64 encoded"
	val ISSUER_SIGNER =  TestSigner(SoftwareKeyPair.fromPrivateKey(base64UrlDecode(ISSUER_PRIVATE_KEY)))

	fun submitDocument() {
		viewModelScope.launch {
			//TODO: allow this credential to be chosen from UI (e.g. to test verification of properties like age)
			// on the check app

			val kb = SoftwareKeyPair()
			val kbValue : Value = Json.decodeFromString(kb.jwkString())

			val sdjwt = SdJwt.create(claims = mapOf<String, Any>(
				//TODO: add necessary exp, nbf, iat claims here, so we can check integrity on the check app
				"vct" to "schema-a",
				"iss" to ISSUER_DID,
				"iat" to 1769179211,
				"given_name" to "Pascal",
				"family_name" to "Tester",
				"credentialNumber" to "0000008765293866749",
				"birth_date" to "19.06.1988",
				"age_over_16" to true,
				"age_over_18" to true,
				"age_over_65" to false,
				"sex" to "m",
				"place_of_origin" to "Brugg",
				"birth_place" to "Brugg",
				"nationality" to "Swiss",
				"personal_administrative_number" to "E11111",
				"age_birth_year" to 1988,
				"document_number" to "E11111",
				"additional_person_info" to "test",
				"verification_type" to "A/V",
				"verification_organization" to "Passb√ºro",
				"issuance_date" to "2026-01-01",
				"issuing_country" to "Switzerland",
				"expiry_date" to "2026-08-01",
				"issuing_authority" to "BJ",
				"portrait" to ""

			).toCbor(),
				disclosures = listOf(
					listOf("given_name").toClaimsPointer()!!,
					listOf("family_name").toClaimsPointer()!!,
					listOf("credentialNumber").toClaimsPointer()!!,
					listOf("portrait").toClaimsPointer()!!,
					listOf("birth_date").toClaimsPointer()!!,
					listOf("age_over_16").toClaimsPointer()!!,
					listOf("age_over_18").toClaimsPointer()!!,
					listOf("age_over_65").toClaimsPointer()!!,

					listOf("sex").toClaimsPointer()!!,
					listOf("place_of_origin").toClaimsPointer()!!,
					listOf("birth_place").toClaimsPointer()!!,
					listOf("nationality").toClaimsPointer()!!,
					listOf("portrait").toClaimsPointer()!!,
					listOf("personal_administrative_number").toClaimsPointer()!!,
					listOf("age_birth_year").toClaimsPointer()!!,
					listOf("document_number").toClaimsPointer()!!,
					listOf("additional_person_info").toClaimsPointer()!!,
					listOf("verification_type").toClaimsPointer()!!,
					listOf("verification_organization").toClaimsPointer()!!,
					listOf("issuing_authority").toClaimsPointer()!!
				),
				//TODO: Ideally we have a pubKeyJwk here and a signer, to actually sign the KB-JWT for verification
				keyId = "$ISSUER_DID#assert-key-01",
				key = ISSUER_SIGNER,
				kbValue)
			//TODO: ideally we would use the dcqlquery's select credential function with a database of tokens (c.f. in the wallet module)
			val credentialQuery = dcqlQuery!!.credentials?.first()!!
			val vpToken = sdjwt!!.getVpToken(
				credentialQuery,
				//TODO: double check what the audience in our flow should be
				"test",
				null,
				null,
				"",
				TestSigner(kb)
			).getOrThrow()
			wallet.submitDocument(Json.encodeToString(mapOf<String, Map<String, List<String>>>(
				// we use the credential query id from the dcql query as the key to identify the credential in the verifier
				// Must match what the verifier expects
				"vp_token" to mapOf(
					credentialQuery.id to listOf(vpToken)
				)

			)).encodeToByteArray())
		}
	}

	var dcqlQuery : DcqlQuery? = null
	var collectingJob : Job? = null
	private fun startCollectingWalletState() {
		collectingJob = viewModelScope.launch {
			wallet.walletState.collect { state ->
				proximityStateMutable.value = state
				if (state is ProximityWalletState.RequestingDocuments) {
					if(state.request is DocumentRequest.OpenId4Vp) {
						//TODO: this is probably signed (or at least it could be signed) we need to check the signature
						// and then check that the 'jwt' jose header contains a wallet attestation attesting to the key used for signing the request
						val request = state.request as DocumentRequest.OpenId4Vp
						val pr: PresentationRequest? = try {

							val presentationRequestString =
								parseEncodedJwtPayload(request.parJwt)


							val v: Value = Json.decodeFromString(presentationRequestString!!)
							PresentationRequest.fromValue(v)

						} catch (e: Exception) {

							// Fallback: try to parse without verifying signature
							PresentationRequest.fromValue(
								Json.decodeFromString<Value>(request.parJwt)
							)

						}

						dcqlQuery = pr?.dcqlQuery
					}
				}
			}
		}
	}
	fun reset() {
		if(::wallet.isInitialized) {
			wallet.disconnect()
			collectingJob?.cancel()
		}

	}

}

class TestSigner(private val kp : SoftwareKeyPair) : SignatureCreator {
	override fun alg(): String {
		return "ES256"
	}

	override fun sign(bytes: ByteArray): ByteArray {
		return kp.signWithKey(bytes)
	}
}